import "./ProductCard.css";

export default function ProductCard({ p = {} }) {
  const title = p.name ?? "상품명";
  const price =
    typeof p.price === "number"
      ? `${p.price.toLocaleString()}원`
      : p.price ?? "";
  const img = Array.isArray(p.images) && p.images[0] ? p.images[0] : "/img/placeholder.";
  const fav = p.favoriteCount ?? 0;

  return (
    <div className="product-card">
      <div className="thumb">
      <img src={img} alt={title} />
      </div>
      <div className="product-body">
        <div className="product-title" title={title}>{title}</div>
        <div className="product-price">{price}</div>
        <div className="product-meta"><span>❤️ {fav}</span></div>
      </div>
    </div>
  );
}
