// src/components/ProductCard.jsx
import "./ProductCard.css";

const PLACEHOLDER = "/img/placeholder.png";

// images ÌïÑÎìúÍ∞Ä Î∞∞Ïó¥/Î¨∏ÏûêÏó¥/ÎπàÍ∞í Îì± Î≠êÍ∞Ä ÏôÄÎèÑ ÏïàÏ†ÑÌïòÍ≤å Ï≤òÎ¶¨
function resolveImage(p) {
  const list = Array.isArray(p.images) ? p.images : (p.images ? [p.images] : []);
  const candidate = list.find((u) => typeof u === "string" && u.trim().length > 0);

  // Ï†àÎåÄ URLÏù¥ ÏïÑÎãàÎ©¥ (http/https/dataÎ°ú Ïïà ÏãúÏûë) Í∑∏ÎÉ• Ïã§Ìå® Ï≤òÎ¶¨Ìï¥ÏÑú placeholder Ïì∞Í≤å
  if (!candidate) return PLACEHOLDER;
  const url = candidate.trim();

  const isAbsolute = /^(https?:)?\/\//i.test(url) || url.startsWith("data:");
  if (!isAbsolute) return PLACEHOLDER; // ÏÉÅÎåÄÍ≤ΩÎ°úÎ©¥ Íπ®Ïßà ÌôïÎ•† ÎÜíÏùå ‚Üí placeholder

  // ÌòºÌï©ÏΩòÌÖêÏ∏† ÎåÄÎπÑ: ÌéòÏù¥ÏßÄÍ∞Ä httpsÏù∏Îç∞ Ïù¥ÎØ∏ÏßÄÍ∞Ä httpÎ©¥ Ï∞®Îã®Îê† Ïàò ÏûàÏùå
  if (typeof window !== "undefined" && window.location.protocol === "https:" && url.startsWith("http://")) {
    return PLACEHOLDER;
  }
  return url;
}

export default function ProductCard({ p = {} }) {
  const title = p.name ?? "ÏÉÅÌíàÎ™Ö";
  const price =
    typeof p.price === "number" ? `${p.price.toLocaleString()}Ïõê` : (p.price ?? "");
  const fav = p.favoriteCount ?? 0;
  const src = resolveImage(p);

  return (
    <article className="product-card">
      <div className="product-card__thumb">
        <img
          src={src}
          alt={title}
          loading="lazy"
          referrerPolicy="no-referrer"  // üîí Ìï´ÎßÅÌÅ¨ Ï∞®Îã® ÌöåÌîºÏóê ÎèÑÏõÄ
          onError={(e) => {             // üîÅ Íπ®ÏßÄÎ©¥ Ï¶âÏãú ÌîåÎ†àÏù¥Ïä§ÌôÄÎçîÎ°ú ÍµêÏ≤¥
            if (e.currentTarget.src !== window.location.origin + PLACEHOLDER) {
              e.currentTarget.src = PLACEHOLDER;
            }
          }}
          style={{ width: "100%", height: "180px", objectFit: "cover" }}
          draggable="false"
        />
      </div>
      <h3 className="product-card__title">{title}</h3>
      <div className="product-card__meta">
        <span className="product-card__price">{price}</span>
        <span className="product-card__fav">‚ù§ {fav}</span>
      </div>
    </article>
  );
}
