import { useEffect, useMemo, useState } from "react";
import ProductCard from "../components/ProductCard";
import "../index.css";
import SearchBar from "../components/SearchBar";
import Pagination from "../components/Pagination";

// 화면폭에 따른 열/페이지 크기 계산
function useResponsive() {
  const getState = () => {
    const w = window.innerWidth;
    const isMobile = w <= 768;
    const isTablet = w <= 1024 && w > 768;
    const bestCols = isMobile ? 1 : isTablet ? 2 : 4;  // 베스트: 1/2/4
    const allCols  = isMobile ? 2 : isTablet ? 3 : 5;  // 전체  : 2/3/5
    const pageSize = allCols * 2;                      // 보이는 줄 수 2줄 기준
    return { bestCols, allCols, pageSize };
  };

  const [state, setState] = useState(getState);
  useEffect(() => {
    const onResize = () => setState(getState());
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);
  return state;
}

const BASE = "https://panda-market-api.vercel.app";

export default function MarketPage() {
  // UI 상태
  const { bestCols, pageSize } = useResponsive();
  const [sort, setSort] = useState("latest");   // 'latest' | 'favorite'
  const [page, setPage] = useState(1);
  const [keyword, setKeyword] = useState("");
  const [typed, setTyped] = useState("");       // 입력값 (디바운스용)

  // 데이터 상태
  const [best, setBest] = useState([]);
  const [list, setList] = useState([]);
  const [totalCount, setTotalCount] = useState(0);
  const [loadingBest, setLoadingBest] = useState(false);
  const [loadingAll, setLoadingAll] = useState(false);

  // 검색 디바운스 (300ms)
  useEffect(() => {
    const t = setTimeout(() => {
      setKeyword(typed);
      setPage(1); // 검색 바뀌면 1페이지로
    }, 300);
    return () => clearTimeout(t);
  }, [typed]);

  // 베스트 상품 (좋아요순)
  useEffect(() => {
    let alive = true;
    async function run() {
      try {
        setLoadingBest(true);
        const url = new URL(`${BASE}/products`);
        url.searchParams.set("page", "1");
        url.searchParams.set("pageSize", String(bestCols));
        url.searchParams.set("sort", "favorite");
        const res = await fetch(url);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();
        const arr = Array.isArray(data?.list) ? data.list : data;
        if (alive) setBest(arr || []);
      } catch (e) {
        console.error("[BestProducts] error:", e);
      } finally {
        if (alive) setLoadingBest(false);
      }
    }
    run();
    return () => { alive = false; };
  }, [bestCols]);

  // 전체 상품
  useEffect(() => {
    let alive = true;
    async function run() {
      try {
        setLoadingAll(true);
        const url = new URL(`${BASE}/products`);
        url.searchParams.set("page", String(page));
        url.searchParams.set("pageSize", String(pageSize));
        if (keyword) url.searchParams.set("keyword", keyword);
        // 최신/좋아요 정렬 (API는 latest/favorite 사용)
        const apiSort = sort === "favorite" ? "favorite" : "latest";
        url.searchParams.set("sort", apiSort);

        const res = await fetch(url);
        if (!res.ok) throw new Error(`HTTP ${res.status}`);
        const data = await res.json();
        const arr = Array.isArray(data?.list) ? data.list : data;

        if (alive) {
          setList(arr || []);
          setTotalCount(typeof data?.totalCount === "number" ? data.totalCount : 0);
        }
      } catch (e) {
        console.error("[ProductList] error:", e);
      } finally {
        if (alive) setLoadingAll(false);
      }
    }
    run();
    return () => { alive = false; };
  }, [page, pageSize, sort, keyword]);

  // 총 페이지 수
  const totalPages = useMemo(() => {
    if (!totalCount) return 1;
    return Math.max(1, Math.ceil(totalCount / pageSize));
  }, [totalCount, pageSize]);

  // 정렬 바뀌면 1페이지로
  function onChangeSort(e) {
    setSort(e.target.value);
    setPage(1);
  }

  return (
    <main className="market main-offset">
      <div className="container">
        {/* 베스트 섹션 */}
        <h2 className="section-title">베스트 상품</h2>
        <div className="grid best-products">
          {loadingBest
            ? Array.from({ length: bestCols }).map((_, i) => <div className="skeleton" key={i} />)
            : best.map((p) => <ProductCard key={p.id} p={p} />)
          }
        </div>

        {/* 툴바: ⬅️검색  ➡️정렬 */}
        <div className="section-header">
  <h2 className="section-title">판매 중인 상품</h2>

  <div className="toolbar toolbar--compact">
    <SearchBar
      className="toolbar__search"
      value={typed}
      onChange={setTyped}
      onSubmit={(v) => setTyped(v)}
      placeholder="검색할 상품을 입력해주세요"
    />

    {/* (선택) 상품 등록 버튼 – 링크/라우팅 연결은 프로젝트에 맞게 */}
        <button type="button" className="btn btn--primary">
          상품 등록하기
        </button>

        <div className="select select--pill">
          <select value={sort} onChange={onChangeSort} aria-label="정렬 옵션">
            <option value="latest">최신순</option>
            <option value="favorite">좋아요순</option>
          </select>
        </div>
      </div>
    </div>

        {/* 전체 상품 */}
        <h2 className="section-title">판매 중인 상품</h2>
        <div className="grid all-products">
          {loadingAll
            ? Array.from({ length: pageSize }).map((_, i) => <div className="skeleton" key={i} />)
            : list.map((p) => <ProductCard key={p.id} p={p} />)
          }
        </div>

        {/* 페이지네이션 */}
        <div className="pagination">
          {Array.from({ length: totalPages }).slice(0, 8).map((_, i) => {
            const n = i + 1;
            return (
              <button
                key={n}
                className={page === n ? "active" : ""}
                onClick={() => setPage(n)}
              >
                {n}
              </button>
            );
          })}
        </div>
      </div>
    </main>
  );
}
