import React, { useState, useEffect } from "react";
import ProductCard from "../components/ProductCard";
import SearchBar from "../components/SearchBar"; // Ïù¥ÎØ∏ ÎßåÎì† SearchBar Î∂àÎü¨Ïò§Í∏∞

const BASE = "https://panda-market-api.vercel.app";

export default function MarketPage() {
  const [bestProducts, setBestProducts] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [keyword, setKeyword] = useState("");
  const [sort, setSort] = useState("recent");

  useEffect(() => {
    // Î≤†Ïä§Ìä∏ ÏÉÅÌíà
    fetch(`${BASE}/products?sort=favorite&page=1&pageSize=4`)
      .then(res => res.json())
      .then(data => setBestProducts(data.list ?? []));

    // Ï†ÑÏ≤¥ ÏÉÅÌíà (Í≤ÄÏÉâ/Ï†ïÎ†¨ Ï†ÅÏö©)
    fetch(`${BASE}/products?sort=${sort}&keyword=${keyword}&page=1&pageSize=10`)
      .then(res => res.json())
      .then(data => setAllProducts(data.list ?? []))
      .finally(() => setLoading(false));
  }, [sort, keyword]);

  return (
    <main className="market">
      <div className="container">
        <h2 className="section-title">Î≤†Ïä§Ìä∏ ÏÉÅÌíà</h2>
        <div className="grid best-products">
          {loading
            ? Array.from({ length: 4 }).map((_, i) => (
                <div className="skeleton" key={i} />
              ))
            : bestProducts.map(p => <ProductCard key={p._id} p={p} />)}
        </div>

        {/* üîç SearchBar.jsx ÌôúÏö© */}
        <div className="toolbar">
          <SearchBar value={keyword} onChange={setKeyword} />
          <select
            className="sort"
            value={sort}
            onChange={e => setSort(e.target.value)}
          >
            <option value="recent">ÏµúÏã† Ïàú</option>
            <option value="favorite">Ï¢ãÏïÑÏöî Ïàú</option>
          </select>
        </div>

        <h2 className="section-title">ÌåêÎß§ Ï§ëÏù∏ ÏÉÅÌíà</h2>
        <div className="grid all-products">
          {loading
            ? Array.from({ length: 10 }).map((_, i) => (
                <div className="skeleton" key={i} />
              ))
            : allProducts.map(p => <ProductCard key={p._id} p={p} />)}
        </div>
      </div>
    </main>
  );
}
