import { useEffect, useState } from "react";
import SearchBar from "../components/SearchBar";
import ProductCard from "../components/ProductCard";
import { getProductList, getBestProducts } from "../services/ProductService";
import "../index.css";

const PAGE_SIZE = 12; // 전체 상품 섹션 페이지 크기

export default function MarketPage() {
  // 베스트
  const [best, setBest] = useState([]);
  const [loadingBest, setLoadingBest] = useState(false);

  // 전체 목록
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");

  // 컨트롤러
  const [page, setPage] = useState(1);
  const [query, setQuery] = useState("");            // 검색어
  const [sort, setSort] = useState("latest");        // 'latest' | 'favorite'

  const totalPages = Math.max(1, Math.ceil(total / PAGE_SIZE));

  // ✅ 베스트(좋아요 순) — 최초 1회
  useEffect(() => {
    (async () => {
      try {
        setLoadingBest(true);
        const list = await getBestProducts(4); // 데스크탑 4열
        setBest(list);
      } finally {
        setLoadingBest(false);
      }
    })();
  }, []);

  // ✅ 전체 상품 — 서버 페이지네이션/검색/정렬
  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        setErr("");
        const { items, total } = await getProductList({
          page,
          pageSize: PAGE_SIZE,
          keyword: query.trim(),
          sort, // 'latest' | 'favorite'
        });
        setItems(items);
        setTotal(total ?? items.length);
      } catch (e) {
        setErr(e.message ?? "목록을 불러오지 못했어요.");
      } finally {
        setLoading(false);
      }
    })();
  }, [page, query, sort]);

  // 핸들러
  const handleSubmitSearch = (v) => { setQuery(v); setPage(1); };
  const handleChangeSort = (v) => { setSort(v); setPage(1); };

  return (
    <main className="market">
      {/* ========== 베스트 상품 ========== */}
      <h2 className="market__section-title">베스트 상품</h2>
      {loadingBest ? (
        <p className="market__status">베스트 불러오는 중...</p>
      ) : (
        <section className="market__grid market__grid--best">
          {best.map((p) => (
            <ProductCard key={p.id ?? p._id ?? `${p.name}-${p.price}`} p={p} />
          ))}
        </section>
      )}

      {/* ========== 툴바(검색/정렬) ========== */}
      <header className="market__toolbar">
        <SearchBar
          value={query}
          onChange={(v) => setQuery(v)}
          onSubmit={handleSubmitSearch}
          placeholder="상품명을 검색해 보세요"
        />

        <label className="market__sort">
          <span>정렬:</span>
          <select
            value={sort}
            onChange={(e) => handleChangeSort(e.target.value)}
            aria-label="정렬 옵션"
          >
            <option value="latest">최신 순</option>
            <option value="favorite">좋아요 순</option>
          </select>
        </label>
      </header>

      {/* ========== 전체 상품 ========== */}
      <h2 className="market__section-title">판매 중인 상품</h2>
      {loading && <p className="market__status">불러오는 중...</p>}
      {err && <p className="market__error">{err}</p>}

      {!loading && !err && (
        <>
          <section className="market__grid">
            {items.map((p) => (
              <ProductCard key={p.id ?? p._id ?? `${p.name}-${p.price}`} p={p} />
            ))}
          </section>

          <nav className="market__pager" aria-label="페이지네이션">
            <button
              type="button"
              disabled={page <= 1}
              onClick={() => setPage((n) => Math.max(1, n - 1))}
            >
              이전
            </button>
            <span className="market__pageinfo">{page} / {totalPages}</span>
            <button
              type="button"
              disabled={page >= totalPages}
              onClick={() => setPage((n) => Math.min(totalPages, n + 1))}
            >
              다음
            </button>
          </nav>
        </>
      )}
    </main>
  );
}
